mapper.select:
SELECT
FROM sys_user WHERE id in
#{item}

mapper.update:
UPDATE sys_user
account = #{account}, password = #{password}, salt = #{salt}, history_pass = #{historyPass}, name = #{name}, idNo = #{idNo}, birthday = #{birthday}, sex = #{sex}, email = #{email}, phone = #{phone}, agent_code = #{agentCode}, dept_code = #{deptCode}, org_code = #{orgCode}, network_code = #{networkCode}, inner_code = #{innerCode}, scenic_id = #{scenicId}, status = #{status}, type = #{type}, creator = #{creator}, create_time = #{createTime}, mender = #{mender}, modify_time = NOW(), is_delete = #{isDelete}, error_login = #{errorLogin},
WHERE id = #{id}

mapper.select:
select
from sys_user where is_delete=0 and agent_code=#{agentCode}
and (phone like CONCAT('%',#{name},'%') or account like CONCAT('%',#{name},'%') or name like CONCAT('%',#{name},'%'))
and network_code IN
#{i}
and create_time > CONCAT(#{beginTime},' 00:00:00')
and create_time < CONCAT(#{endTime},' 23:59:59')
limit #{num},#{pageSize}

select count(*) from sys_user where is_delete=0 and agent_code=#{agentCode}
and (phone like CONCAT('%',#{name},'%') or account like CONCAT('%',#{name},'%') or name like CONCAT('%',#{name},'%'))
and network_code IN
#{i}
and create_time > CONCAT(#{beginTime},' 00:00:00')
and create_time < CONCAT(#{endTime},' 23:59:59')

mapper.update:
update sys_user
set  is_delete = #{isDelete}
where agent_code = #{agentCode}

